import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import Navbar from '../Component/Navbar';
import Docs from './Docs';

const Detail = () => {
    let { _id } = useParams()

    const [detay, setDetay] = useState([])

    useEffect(() => {
        const api_link = `https://api.disneyapi.dev/character/${_id}`
        axios.get(api_link)
            .then(response => {
                setDetay(response.data)
            })
    }, [_id])


    console.log(detay, "detay");
    return (
        <><br />
            <Navbar />
            <li>{detay.data?.name}</li>
            <img style={{ width: 500 }} src={detay.data?.imageUrl} />

            <table class="table">
                <thead>
                    <tr>
                        <th scope="col">Id</th>
                        <th scope="col">Name</th>
                        <th scope="col">Url</th>
                        <th scope="col">Handle</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <th scope="row">{detay.data?._id}</th>
                        <td>{detay.data?.name}</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                    </tr>
                </tbody>
            </table>







        </>
    )
}

export default Detail;
