import React, { useEffect, useState } from 'react'
import Navbar from '../Component/Navbar';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const RickyDetail = () => {

    let { id } = useParams()
    const [detay, setDetay] = useState([])

    useEffect(() => {
        const api_Link = `https://rickandmortyapi.com/api/character/${id}`
        axios.get(api_Link)
            .then(response => {
                setDetay(response.data)
            })
    }, [id])

    console.log(detay,"detay");

    return (
        <><br/>
            <Navbar /><br/><br/><br/><br/><br/><br/>
            <table class="table">
                <thead>
                    <tr>
                        <th scope="col">Id</th>
                        <th scope="col">İsim</th>
                        <th scope="col">Url</th>
                        <th scope="col">Resim</th>
                        <th scope='col'>Film İsmi</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <th scope="row">{detay.id}</th>
                        <td>{detay.name}</td>
                        <td>{detay.url}</td>
                        <td><img src={detay.image} /></td>
                        <td>{detay.origin.name}</td>
                    </tr>
                </tbody>
            </table>



        </>
    )
}

export default RickyDetail;
